    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
       String p,q;
     
       int p1=0,q1=0,j=0,m1[],i=0,m11;
        p =jComboBox1.getSelectedItem().toString();
        q =jComboBox2.getSelectedItem().toString();
        p1=Integer.parseInt(p);
        q1=Integer.parseInt(q);
        n=p1 * q1;//n=p*q
        m=(p1-1)*(q1-1);//m=(p-1)*(q-1)
        rTxt.setText("" + n);//顯示n
        rTxt1.setText("" + m);//顯示m
        m1=new int [m];
       for(i=0;i < m;i++)
    {m11=gcd(m,i);//找出小於m且與m互質的數(和m的最大公因數為1)
       if(m11==1&&i>1&&i!=p1&&i!=q1) 
       {
           m1[i]=i;
           jComboBox3.addItem(m1[i]);//加到combobox的item中
       }  
    } 
       
    }//GEN-LAST:event_jButton2ActionPerformed
  
    public static int gcd(int m, int par){ 
 int gcd=0; 
     if(par==0)
        {  
            gcd=m;
        }
         else
        {  
            gcd=gcd(par,m%par);  
        }
         return gcd;
}
