  public void getPrivate_key()
     {
         long value=1;
        private_key=0;
         outer:for(long i=1;;i++)
         {
             value=i*m+1;
             if((value%key1==0)&& 
            		 (value/key1 < m))
             {
                 private_key=(int) (value/key1);
                 break outer;
             }
         }
         rTxt4.setText(""+private_key);
     }
